import framebuf
def show_img(oled, byte_arr):
    fb = framebuf.FrameBuffer(byte_arr, 128, 64, framebuf.MONO_HLSB)
    oled.blit(fb, 0, 0)

def int32_to_bytes(int32list):
    barr=[]
    for int32 in int32list:
        for bytes in int(int32).to_bytes(4, 'big'):
            barr.append(bytes)
    return bytearray(barr)

